import React, {Component,Fragment} from 'react';
import {Col, Container, Row, Button} from "react-bootstrap";
import myimage from '../../asset/image/my-image.jpg';
class AboutDescription extends Component {
    render() {
        return (
            <Fragment>
                <Container className="mt-5">
                    <Row>
                        <Col sm={12} lg={6} md={12}>
                        <img className="myimage" src={myimage}/> 
                        </Col>
                        <Col sm={12} lg={6} md={12}>
                            <h1 className="aboutme">About Me</h1>  
                            <p>Name: Ridoy Chandra Sarker</p>
                            <p>Address: Kazipara , Mirpur-10 , Dhaka</p>
                            <p>Zip code: 1216</p>
                            <p>Email: csridoy42@gmail.com</p>
                            <p>Phone: +8801883448329</p>
                            <p>Github: https://github.com/RidoySarker</p>
                            <a href="../../asset/image/Ridoy_Chandra_Sarker.pdf" className="btn btn-primary">DOWNLOAD CV</a>
                        </Col>
                    </Row>
                </Container>
            </Fragment>
        );
    }
}

export default AboutDescription;
